sum by (applied_virtualmachineclass) (
    max by (namespace, name, applied_virtualmachineclass) (
        d8_virtualization_virtualmachine_info{namespace=~"$namespace"}
    )
    * on(namespace, name) group_left()
    (
        max by (namespace, name) (
            d8_virtualization_virtualmachine_status_phase{namespace=~"$namespace", phase!~"(Pending|Stopped)"} == 1
        )
    )
)

